import axios from 'axios';
import './App.css';
function App() {
  const [movie,setMovie] = useState([]);
  const getItem = ()=>{
    axios.get(`http://localhost:3000/posts`).then(res=>{
      setMovie(res.data)
    })
  }
  useEffect(() => 
 { getItem()
  },[])
