    title:""
  })

  const addItem = (e) =>{
    e.preventDefault();
    axios.post(`http://localhost:3000/posts`,item).then(res=>{
      console.log(res.data);
    })
  }
  const deleteItem = (id) =>{
    axios.delete(`http://localhost:3000/posts/${id}`,).then(res=>{
console.log(res.data);
    })
  }
  return (
    <div className="App">
      {movie.map(el=>(
        <div> 
          <h1>{el.title}</h1>
          <button onclick={()=>deleteItem(el.id)}>delete</button>

        </div>
      ))}
      <form onSubmit={addItem}>
        <input type="text"  onChange={(e)=>{setItem({...item,title:e.target.value})}}/>
     <input type="submit" />
      </form>
    </div>
  );
}

export default App;
