import axios from 'axios';
import './App.css';
function App() {
  const [movie,setMovie] = useState([]);
 const getItems = ()=>{
   axios.get(`http://localhost:3000/posts`).then(res=>{
     setMovie(res.data)
   })
 }

 useEffect(()=>{
   getItems()
 },[])
 const[item,setItem]=useState({
   title:"",
   para:""
 })
 const addItem = ()=>{
   axios.post(`http://localhost:3000/posts`,item).then(res=>{
     console.log(res.data)
   })
 } 
  return (
    <div className="App">
 {movie.map(el=>(
   <div> 

     <h1>{el.title}</h1>
     <p>{el.para}</p>
   </div>
 ))}
 <form>
   <input type="text" placeholder="title" onChange={()=>setItem} />
   <input type="text" placeholder="paragraphe" />
<input type="submit" />

 </form>
    </div>
  );
}

export default App;
