import axios from 'axios';

     export const getAll = () => {
       return dispatch => {
           axios.get(`http://localhost:3000/posts`) .then(all =>dispatch (
               {type: 'GET_ALL_SUCCEED',payload: all}) 
           )

           .catch(err=>dispatch( {
               type: "GET_ALL_FAILED",
               payload: err
             }))
       }

   }
