 const itemState = {
    item:[]
};

export  const itemReducer = (state = itemState, action ) => {
    const { type, payload } = action;
    switch( type) {
        // get All item
        case   'GET_ALL_SUCCEED' :
            return {
                ...state,
                item: payload 
              }
             break;
    }
return state
};
